def arrayToImg(arr, width, height, col=[255,0,255]): #
    imgArray = []
    if len(arr) == width*height / 8: #single-color mode
        counter = 0
        row = []
        for byte in arr:
            for i in range(8):
                bit = byte >> 7-i & 1
                pixel = col if bit else [0,0,0]
                row.append(pixel)
            counter += 8
            
            if counter % width == 0:
                imgArray.append(row)
                row = []
    else: #RGB mode
        counter = 0
        row = []
        for i in range(int(len(arr)/3)):
            r = arr[i*3]
            g = arr[i*3+1]
            b = arr[i*3+2]
            row.append([r,g,b])
            counter += 1

            if counter % width == 0:
                imgArray.append(row)
                row = []
    return Image.fromarray(np.uint8(imgArray)).convert("RGB")

