def reflectImg(img): #Reflect image so that it displays correctly on both panels
    img_out = Image.new('RGB', (img.width*2, img.height))
    img_out.paste(ImageOps.mirror(img), (img.width,0))
    img_out.paste(img, (0, 0))
    return img_out

